select DISTINCT tpp.id projectId , tpp.`name` projectName, tpp.leader projectManagerId, tpt.id teamId, tpt.`name` teamName, tpt.leader teamLeaderId, tptu.user_id userId from t_project_project tpp,t_project_team tpt,t_project_team_user tptu where tpp.id = tpt.project_id and tpt.id = tptu.team_id
and tpp.id = #{projectId,jdbcType=BIGINT}
and tpt.id = #{teamId,jdbcType=BIGINT}
and tptu.user_id = #{userId,jdbcType=BIGINT}

select DISTINCT tpp.id projectId , tpp.`name` projectName, tpp.leader projectManagerId,
tpt.id teamId, tpt.`name` teamName, tpt.leader teamLeaderId,
tptu.user_id userId from t_project_project tpp,t_project_team tpt,t_project_team_user tptu where tpp.id = tpt.project_id and tpt.id = tptu.team_id
and tpp.id = #{projectId,jdbcType=BIGINT}
and tpt.id = #{teamId,jdbcType=BIGINT}
and tptu.user_id = #{userId,jdbcType=BIGINT}

select tppc.*, tpp.`name` projectName, tps.`name` projectStepStr, tpm.`name` milestoneName, case when tppc.priority = "1" then "" when tppc.priority = "2" then "" when tppc.priority = "3" then "" else "" end priorityStr,
case when tppc.check_status = "1" then "" when tppc.check_status = "2" then "" when tppc.check_status = "3" then "" when tppc.check_status = "4" then "" else "" end checkStatusStr
from t_project_plan_create tppc LEFT JOIN t_project_project tpp ON tpp.id = tppc.project_id LEFT JOIN t_project_step tps ON tps.id = tppc.project_step LEFT JOIN t_project_milestone tpm ON tpm.id = tppc.plan_milestone_id where tppc.parent_plan_id is null and tppc.del_status = ${@com.fea.project.enums.base.DeleteStatusEnum@NOT_DELETE.getStatus()} and tppc.create_id = #{model.createId,jdbcType=BIGINT} and tppc.plan_leader_id = #{model.createId,jdbcType=BIGINT} and tppc.project_id = #{model.projectId,jdbcType=BIGINT}
and tppc.project_step = #{model.projectStep,jdbcType=BIGINT}
order by tppc.plan_startTime desc

